import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-301 Legal rate of interest; when legal rate implied.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-301.md","filePath":"vacode/6.2-301.md"}'),i={name:"vacode/6.2-301.md"},n=r('<h1 id="§-6-2-301-legal-rate-of-interest-when-legal-rate-implied" tabindex="-1">§ 6.2-301 Legal rate of interest; when legal rate implied. <a class="header-anchor" href="#§-6-2-301-legal-rate-of-interest-when-legal-rate-implied" aria-label="Permalink to &quot;§ 6.2-301 Legal rate of interest; when legal rate implied.&quot;">​</a></h1><p>A. The legal rate of interest shall be an annual rate of six percent.</p><p>B. Except as provided in subsection (b) of § <a href="http://law.lis.virginia.gov/vacode/8.3A-112/" target="_blank" rel="noreferrer">8.3A-112</a> and § <a href="http://law.lis.virginia.gov/vacode/6.2-302/" target="_blank" rel="noreferrer">6.2-302</a>, the legal rate of interest shall be implied when there is an obligation to pay interest and no express contract to pay interest at a specified rate.</p><p>C. The seller or provider of goods sold or services provided on an open account shall be entitled to, and may collect, interest at the legal rate upon the unpaid balance if (i) there exists no written agreement for closed-end credit under § <a href="http://law.lis.virginia.gov/vacode/6.2-311/" target="_blank" rel="noreferrer">6.2-311</a> or open-end credit plan under § <a href="http://law.lis.virginia.gov/vacode/6.2-312/" target="_blank" rel="noreferrer">6.2-312</a> and (ii) the purchaser or recipient of the goods or services fails to make payment in full within 60 days after mailing or presentation of a billing statement or invoice. Such interest shall begin to accrue on the day following such 60-day period.</p><p>1987, c. 622, § 6.1-330.53; 1991, c. 375, § 6.1-330.77:1; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),o=[n];function l(s,c,d,p,h,g){return a(),t("div",null,o)}const v=e(i,[["render",l]]);export{_ as __pageData,v as default};
