import{_ as e,o as t,c as n,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 6.2-313 Open-end credit extended by banks or savings institutions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-313.md","filePath":"vacode/6.2-313.md"}'),r={name:"vacode/6.2-313.md"},i=a('<h1 id="§-6-2-313-open-end-credit-extended-by-banks-or-savings-institutions" tabindex="-1">§ 6.2-313 Open-end credit extended by banks or savings institutions. <a class="header-anchor" href="#§-6-2-313-open-end-credit-extended-by-banks-or-savings-institutions" aria-label="Permalink to &quot;§ 6.2-313 Open-end credit extended by banks or savings institutions.&quot;">​</a></h1><p>A. Notwithstanding any statutory or case law, any bank or savings institution may impose finance charges and other charges and fees at such rates and in such amounts and manner as may be agreed by the borrower under an open-end credit plan.</p><p>B. In the event of the extension of credit by a bank or savings institution hereunder to be effected by the use of a credit card for the purchase of merchandise or services, no finance charge shall be imposed upon the cardholder or borrower on such extension of credit if payment in full of the unpaid balance owing for all extensions of credit under the open-end credit plan is received at the place designated by the creditor prior to the payment due date, which shall be at least 25 days later than the billing date.</p><p>1987, cc. 622, 639, 714, § 6.1-330.63; 1992, Sp. Sess., c. 4; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0112" target="_blank" rel="noreferrer">112</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0454" target="_blank" rel="noreferrer">454</a>.</p>',4),o=[i];function s(d,c,l,h,p,g){return t(),n("div",null,o)}const _=e(r,[["render",s]]);export{b as __pageData,_ as default};
