import{_ as o,o as e,c as s,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 6.2-317 Loans of $5,000 or more for business or investment purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-317.md","filePath":"vacode/6.2-317.md"}'),t={name:"vacode/6.2-317.md"},n=r('<h1 id="§-6-2-317-loans-of-5-000-or-more-for-business-or-investment-purposes" tabindex="-1">§ 6.2-317 Loans of $5,000 or more for business or investment purposes. <a class="header-anchor" href="#§-6-2-317-loans-of-5-000-or-more-for-business-or-investment-purposes" aria-label="Permalink to &quot;§ 6.2-317 Loans of $5,000 or more for business or investment purposes.&quot;">​</a></h1><p>A. For purposes of this section:</p><ol><li><p>A loan shall be deemed to be for business or investment purposes if it is not for personal, family, or household purposes; and</p></li><li><p>Personal, family, or household purposes do not include a passive or active investment.</p></li></ol><p>B. No person shall, by way of defense or otherwise, avail himself of the provisions of this chapter, or any other statutory or case law relating to usury or compounding of interest, to avoid or defeat the payment of interest, or any other sum, in connection with a loan made to a person for business or investment purposes, if the initial amount of the loan is $5,000 or more.</p><p>1987, c. 622, § 6.1-330.75; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),a=[n];function i(p,l,f,c,u,m){return e(),s("div",null,a)}const _=o(t,[["render",i]]);export{h as __pageData,_ as default};
