import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-326 Fees and charges in connection with loans by real estate lenders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-326.md","filePath":"vacode/6.2-326.md"}'),r={name:"vacode/6.2-326.md"},o=t('<h1 id="§-6-2-326-fees-and-charges-in-connection-with-loans-by-real-estate-lenders" tabindex="-1">§ 6.2-326 Fees and charges in connection with loans by real estate lenders. <a class="header-anchor" href="#§-6-2-326-fees-and-charges-in-connection-with-loans-by-real-estate-lenders" aria-label="Permalink to &quot;§ 6.2-326 Fees and charges in connection with loans by real estate lenders.&quot;">​</a></h1><p>A. A lender engaged in making real estate mortgage or deed of trust loans, other than loans subject to the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/6.2-327/" target="_blank" rel="noreferrer">6.2-327</a> and <a href="http://law.lis.virginia.gov/vacode/6.2-328/" target="_blank" rel="noreferrer">6.2-328</a>, may:</p><ol><li><p>Charge or collect in advance from the borrower a loan fee as agreed between the parties; and</p></li><li><p>Require the borrower to pay the reasonable and necessary charges in connection with making the loan, including the cost of title examination, title insurance, recording and filing fees, taxes, insurance, including mortgage guaranty insurance, appraisals, credit reports, surveys, drawing of papers, and closing the loan.</p></li></ol><p>B. The fees and charges permitted by this section and other sections of this chapter are in addition to those permitted by § <a href="http://law.lis.virginia.gov/vacode/6.2-325/" target="_blank" rel="noreferrer">6.2-325</a> and may be added to the principal of the loan, and shall not be considered in determining whether a loan contract is usurious.</p><p>1987, c. 622, § 6.1-330.70; 1990, c. 3; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),i=[o];function s(l,c,d,h,g,p){return a(),n("div",null,i)}const b=e(r,[["render",s]]);export{f as __pageData,b as default};
