import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-329 Loans insured or guaranteed by certain governmental agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-329.md","filePath":"vacode/6.2-329.md"}'),o={name:"vacode/6.2-329.md"},r=n('<h1 id="§-6-2-329-loans-insured-or-guaranteed-by-certain-governmental-agencies" tabindex="-1">§ 6.2-329 Loans insured or guaranteed by certain governmental agencies. <a class="header-anchor" href="#§-6-2-329-loans-insured-or-guaranteed-by-certain-governmental-agencies" aria-label="Permalink to &quot;§ 6.2-329 Loans insured or guaranteed by certain governmental agencies.&quot;">​</a></h1><p>A. No person shall, by way of defense or otherwise, avail himself of any of the provisions of this chapter or any other law relating to usury or any statutory or case law relating to compounding of interest to avoid or defeat the payment of any interest or any other sum which he has contracted to pay on any loan:</p><ol><li><p>Insured by the Federal Housing Administration, pursuant to the provisions of the National Housing Act (12 U.S.C. § 1701 et seq.);</p></li><li><p>Guaranteed by the U.S. Department of Veterans Affairs, pursuant to Title 38 of the United States Code; or</p></li><li><p>Insured or guaranteed by any similar federal governmental agency or organization, or made directly or indirectly by the Virginia Housing Development Authority pursuant to the provisions of Chapter 1.2 (§ <a href="http://law.lis.virginia.gov/vacode/36-55.24/" target="_blank" rel="noreferrer">36-55.24</a> et seq.) of Title 36.</p></li></ol><p>B. Nothing contained in this chapter shall be construed to prevent the recovery of such interest or any other sum from any person who has contracted to pay the same in connection with any loan described in this section.</p><p>1987, c. 622, § 6.1-330.74; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),i=[r];function s(c,l,d,h,p,u){return t(),a("div",null,i)}const y=e(o,[["render",s]]);export{f as __pageData,y as default};
