import{_ as e,o as r,c as a,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-411 Requirements relating to insurance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-411.md","filePath":"vacode/6.2-411.md"}'),t={name:"vacode/6.2-411.md"},i=n('<h1 id="§-6-2-411-requirements-relating-to-insurance" tabindex="-1">§ 6.2-411 Requirements relating to insurance. <a class="header-anchor" href="#§-6-2-411-requirements-relating-to-insurance" aria-label="Permalink to &quot;§ 6.2-411 Requirements relating to insurance.&quot;">​</a></h1><p>A. Any lender making a loan secured by a subordinate mortgage or deed of trust, as defined in § <a href="http://law.lis.virginia.gov/vacode/6.2-300/" target="_blank" rel="noreferrer">6.2-300</a>, may require the borrower to provide:</p><ol><li><p>Evidence of flood insurance if the security property is located in a special flood hazard area;</p></li><li><p>Evidence of fire and extended coverage insurance; and</p></li><li><p>Decreasing term life insurance, in an amount not exceeding the amount of the loan and for a period not exceeding the term of the loan.</p></li></ol><p>B. At the option of the borrower, accident and health insurance and involuntary unemployment insurance may be provided by the lender.</p><p>C. Proof of all insurance issued in connection with loans subject to this chapter shall be furnished to the borrower within 10 days from the date the loan is closed.</p><p>1987, c. 622, § 6.1-330.72; 1993, c. 774; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0243" target="_blank" rel="noreferrer">243</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),o=[i];function l(c,s,d,p,h,f){return r(),a("div",null,o)}const _=e(t,[["render",l]]);export{u as __pageData,_ as default};
