import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-412 Insurance coverage under certain loans not to exceed replacement value of improvements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-412.md","filePath":"vacode/6.2-412.md"}'),o={name:"vacode/6.2-412.md"},n=t('<h1 id="§-6-2-412-insurance-coverage-under-certain-loans-not-to-exceed-replacement-value-of-improvements" tabindex="-1">§ 6.2-412 Insurance coverage under certain loans not to exceed replacement value of improvements. <a class="header-anchor" href="#§-6-2-412-insurance-coverage-under-certain-loans-not-to-exceed-replacement-value-of-improvements" aria-label="Permalink to &quot;§ 6.2-412 Insurance coverage under certain loans not to exceed replacement value of improvements.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Flood insurance coverage&quot; means insurance against loss or damage to any property caused by flooding or the rising of the waters of the ocean or its tributaries.</p><p>&quot;Property insurance coverage&quot; means insurance against losses or damages caused by perils that commonly are covered in insurance policies described with terms similar to &quot;standard fire&quot; or &quot;standard fire with extended coverage.&quot;</p><p>B. No lender shall require a borrower, as a condition to receiving or maintaining a loan secured by any mortgage or deed of trust, to provide or purchase property insurance coverage or flood insurance coverage against risks to any improvements on any real property in an amount exceeding the replacement value of the improvements on the real property.</p><p>C. In determining the replacement value of the improvements on any real property, the lender may:</p><ol><li><p>Accept the value placed on the improvements by the insurer; or</p></li><li><p>Use the value placed on the improvements that is determined by the lender&#39;s appraisal of the real property.</p></li></ol><p>D. A violation of this section shall not affect the validity of the mortgage or deed of trust securing the loan.</p><p>1989, c. 230, § 6.1-2.6:1; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0247" target="_blank" rel="noreferrer">247</a>.</p>',9),i=[n];function s(c,l,p,d,u,m){return r(),a("div",null,i)}const g=e(o,[["render",s]]);export{v as __pageData,g as default};
