import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-421 Certain contracts to permit prepayment; amount of prepayment penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-421.md","filePath":"vacode/6.2-421.md"}'),r={name:"vacode/6.2-421.md"},n=o('<h1 id="§-6-2-421-certain-contracts-to-permit-prepayment-amount-of-prepayment-penalty" tabindex="-1">§ 6.2-421 Certain contracts to permit prepayment; amount of prepayment penalty. <a class="header-anchor" href="#§-6-2-421-certain-contracts-to-permit-prepayment-amount-of-prepayment-penalty" aria-label="Permalink to &quot;§ 6.2-421 Certain contracts to permit prepayment; amount of prepayment penalty.&quot;">​</a></h1><p>A. For the purpose of this section:</p><ol><li><p>&quot;First deed of trust&quot; or &quot;first mortgage&quot; includes all deeds of trust and mortgages, and amendments thereto, that are made by the same grantor or mortgagor, secure notes held by the same holder, convey substantially the same real estate, and are superior to all other deeds of trust or mortgages on the real estate; and</p></li><li><p>&quot;Real estate&quot; includes a leasehold estate of not less than 25 years.</p></li></ol><p>B. Every loan contract, except as provided in subsection D, that is secured by a first deed of trust or first mortgage on real estate if the principal amount of the loan is less than $75,000, shall:</p><ol><li><p>Permit the prepayment of the unpaid principal at any time; and</p></li><li><p>Not provide for a prepayment penalty in excess of one percent of the unpaid principal balance.</p></li></ol><p>C. Any prepayment penalty provision in violation of subdivision B 2 shall be unenforceable as to the amount in excess of one percent of such balance.</p><p>D. The provisions of:</p><ol><li><p>Subsections B and C shall not apply to secured or unsecured notes evidencing installment sales contracts; and</p></li><li><p>Subdivision B 2 and subsection C shall not apply to any loan contract that is (i) subject to § <a href="http://law.lis.virginia.gov/vacode/6.2-422/" target="_blank" rel="noreferrer">6.2-422</a> or <a href="http://law.lis.virginia.gov/vacode/6.2-1409/" target="_blank" rel="noreferrer">6.2-1409</a> or (ii) governmentally regulated as to prepayment privilege.</p></li></ol><p>1987, c. 622, §§ 6.1-330.69, 6.1-330.81; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',9),i=[n];function s(p,l,c,d,m,u){return t(),a("div",null,i)}const y=e(r,[["render",s]]);export{f as __pageData,y as default};
