import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-424 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-424.md","filePath":"vacode/6.2-424.md"}'),r={name:"vacode/6.2-424.md"},i=o('<h1 id="§-6-2-424-definitions" tabindex="-1">§ 6.2-424 Definitions. <a class="header-anchor" href="#§-6-2-424-definitions" aria-label="Permalink to &quot;§ 6.2-424 Definitions.&quot;">​</a></h1><p>As used in this article, unless the context otherwise requires:</p><p>&quot;Cardholder&quot; means the person or organization named on the face of a credit card to whom or for whose benefit the credit card was issued by an issuer.</p><p>&quot;Credit card&quot; means any instrument or device, whether known as a credit card, credit plate, or by any other name, issued with or without fee by an issuer for the use of the cardholder in obtaining money, goods, services, or any other thing of value.</p><p>&quot;Issuer&quot; means the business organization or financial institution or its duly authorized agent that issues a credit card.</p><p>&quot;Payment device&quot; means any credit card, any &quot;accepted card or other means of access&quot; as defined in 15 U.S.C. § 1693a(1), or any card that enables a person to pay for transactions through the use of value stored on the card itself.</p><p>1970, c. 324, § <a href="http://law.lis.virginia.gov/vacode/11-30/" target="_blank" rel="noreferrer">11-30</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',7),n=[i];function s(c,d,h,u,f,l){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
