import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 6.2-429 Improper use of payment device numbers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-429.md","filePath":"vacode/6.2-429.md"}'),r={name:"vacode/6.2-429.md"},n=o('<h1 id="§-6-2-429-improper-use-of-payment-device-numbers" tabindex="-1">§ 6.2-429 Improper use of payment device numbers. <a class="header-anchor" href="#§-6-2-429-improper-use-of-payment-device-numbers" aria-label="Permalink to &quot;§ 6.2-429 Improper use of payment device numbers.&quot;">​</a></h1><p>A. No person that accepts payment devices for any purpose shall print on any receipt provided to the holder of the payment device (i) more than the last four digits of the payment device number or (ii) the expiration date.</p><p>B. For transactions in which the sole means of recording the person&#39;s payment device number is by handwriting or by an imprint or copy of the payment device, no receipt, other than the one original, shall display the information prohibited in subsection A. Returning all copies, including carbons, that do not comply with this section, to the payment device holder or authorized user or destroying such copies and carbons in front of the payment device holder or authorized user shall constitute compliance with this section.</p><p>C. The provisions of this section shall apply to all cash registers or other machines or devices that electronically print receipts for payment device transactions that are placed in service on or after July 1, 2003.</p><p>D. For all cash registers or other machines or devices that electronically print receipts for payment device transactions in service prior to July 1, 2003, the provisions of this subsection shall not apply until July 1, 2005.</p><p>E. Any person violating this section (i) shall be liable to the payment device holder and the issuer for any damages or expenses, or both, including attorney fees, that the payment device holder incurs due to the use of the payment device without the permission of the payment device holder and (ii) may be compelled, in a proceeding instituted in any appropriate court by the attorney for the Commonwealth, to comply with this section by injunction, mandamus, or other appropriate remedy. Without limiting the remedies authorized by this section in a proceeding instituted by the attorney for the Commonwealth, any person failing, neglecting, or refusing to obey any injunction, mandamus, or other remedy obtained pursuant to this section, shall be subject, in the discretion of the court, to a civil penalty not to exceed $1,000 for each violation.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>, § 11-33.2; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0373" target="_blank" rel="noreferrer">373</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',7),a=[n];function s(c,p,h,l,d,m){return t(),i("div",null,a)}const f=e(r,[["render",s]]);export{y as __pageData,f as default};
