import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 6.2-605 Applicability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-605.md","filePath":"vacode/6.2-605.md"}'),o={name:"vacode/6.2-605.md"},r=i('<h1 id="§-6-2-605-applicability" tabindex="-1">§ 6.2-605 Applicability. <a class="header-anchor" href="#§-6-2-605-applicability" aria-label="Permalink to &quot;§ 6.2-605 Applicability.&quot;">​</a></h1><p>A. The provisions of §§ <a href="/vacode/6.2-606/">6.2-606</a>, <a href="/vacode/6.2-607/">6.2-607</a>, and <a href="/vacode/6.2-608/">6.2-608</a> concerning beneficial ownership as between parties, or as between parties and P.O.D. payees or beneficiaries of multiple-party accounts, are relevant only to controversies between these persons and their creditors and other successors, and have no bearing on the power of withdrawal of these persons as determined by the terms of account contracts.</p><p>B. The provisions of §§ <a href="/vacode/6.2-612/">6.2-612</a> through <a href="/vacode/6.2-617/">6.2-617</a> govern the liability of financial institutions that make payments pursuant thereto, and their set-off rights, but shall have no effect on the beneficial ownership of or the power of withdrawal from the accounts between the parties or P.O.D. payees or beneficiaries of multiple-party accounts and shall have no effect on the fiduciary duties or obligations of fiduciaries under the governing instrument of multiple-fiduciary accounts.</p><p>1979, c. 407, § 6.1-125.2; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0259" target="_blank" rel="noreferrer">259</a>.</p>',4),n=[r];function s(c,l,p,h,f,d){return a(),t("div",null,n)}const b=e(o,[["render",s]]);export{u as __pageData,b as default};
