import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-614 Payment of P.O.D. account.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-614.md","filePath":"vacode/6.2-614.md"}'),r={name:"vacode/6.2-614.md"},n=o('<h1 id="§-6-2-614-payment-of-p-o-d-account" tabindex="-1">§ 6.2-614 Payment of P.O.D. account. <a class="header-anchor" href="#§-6-2-614-payment-of-p-o-d-account" aria-label="Permalink to &quot;§ 6.2-614 Payment of P.O.D. account.&quot;">​</a></h1><p>Any P.O.D. account may be paid, on request, to any original party to the account. Payment may be made, on request, to the P.O.D. payee or to the personal representative or heirs of a deceased P.O.D. payee under the Virginia Small Estate Act (§ <a href="http://law.lis.virginia.gov/vacode/64.2-600/" target="_blank" rel="noreferrer">64.2-600</a> et seq.) upon presentation to the financial institution of proof of death showing that the P.O.D. payee survived all persons named as original payees. Payment may be made to the personal representative or heirs of a deceased original payee under the Virginia Small Estate Act (§ <a href="http://law.lis.virginia.gov/vacode/64.2-600/" target="_blank" rel="noreferrer">64.2-600</a> et seq.) if proof of death is presented to the financial institution showing that his decedent was the survivor of all other persons named on the account either as an original payee or as P.O.D. payee.</p><p>1979, c. 407, § 6.1-125.11; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',3),i=[n];function s(c,l,p,h,d,f){return a(),t("div",null,i)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
