import{_ as o,o as a,c as t,O as e}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-830 Conversion from stock association to bank; conversion from bank to stock association.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-830.md","filePath":"vacode/6.2-830.md"}'),n={name:"vacode/6.2-830.md"},i=e('<h1 id="§-6-2-830-conversion-from-stock-association-to-bank-conversion-from-bank-to-stock-association" tabindex="-1">§ 6.2-830 Conversion from stock association to bank; conversion from bank to stock association. <a class="header-anchor" href="#§-6-2-830-conversion-from-stock-association-to-bank-conversion-from-bank-to-stock-association" aria-label="Permalink to &quot;§ 6.2-830 Conversion from stock association to bank; conversion from bank to stock association.&quot;">​</a></h1><p>A. A state stock association may be converted into a bank upon compliance with the procedure set forth in § <a href="http://law.lis.virginia.gov/vacode/6.2-1144/" target="_blank" rel="noreferrer">6.2-1144</a>.</p><p>B. A bank may be converted into a stock association by the amendment of its articles of incorporation in compliance with the procedure established by Title 13.1, provided that such conversion is approved in advance by the Commission. Prior to approving or disapproving a conversion, the Commission shall investigate the application to convert as if it was an application for a certificate of authority to begin a savings and loan business, and approval shall not be granted unless the applicant meets the standards established by § <a href="http://law.lis.virginia.gov/vacode/6.2-1118/" target="_blank" rel="noreferrer">6.2-1118</a>. Within one year of the date of the conversion, the resulting stock association shall conform its assets and operations to the provisions of law regulating the operation of savings and loan associations. The Commission may grant such resulting stock association additional one-year periods, not to exceed a total of four additional years, in which to conform its assets and operations to the provisions of law regulating the operation of savings and loan associations.</p><p>1982, c. 224, § 6.1-195.57:2; 1985, c. 425, § 6.1-194.38; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),s=[i];function r(c,l,d,p,h,f){return a(),t("div",null,s)}const k=o(n,[["render",r]]);export{m as __pageData,k as default};
