import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 6.2-834 Operation of branch office under different name; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-834.md","filePath":"vacode/6.2-834.md"}'),t={name:"vacode/6.2-834.md"},r=n('<h1 id="§-6-2-834-operation-of-branch-office-under-different-name-civil-penalty" tabindex="-1">§ 6.2-834 Operation of branch office under different name; civil penalty. <a class="header-anchor" href="#§-6-2-834-operation-of-branch-office-under-different-name-civil-penalty" aria-label="Permalink to &quot;§ 6.2-834 Operation of branch office under different name; civil penalty.&quot;">​</a></h1><p>A. No branch office shall be operated or advertised under any other name than that of the identical name of the bank, unless (i) permission is first obtained from the Commission and (ii) the different name shall contain or have added thereto language clearly indicating that it is a branch office of the bank or a division of the bank.</p><p>B. The Commission may impose a civil penalty not exceeding $2,000 upon any bank that it determines, in proceedings commenced in accordance with the Commission&#39;s Rules, has violated the provisions of this section.</p><p>Code 1950, §§ 6-28, 6-29; 1966, c. 584, §§ 6.1-41, 6.1-113; 1979, c. 59; 1987, c. 556; 1992, c. 136; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0007" target="_blank" rel="noreferrer">7</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0130" target="_blank" rel="noreferrer">130</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>.</p>',4),o=[r];function c(f,l,s,d,h,p){return a(),i("div",null,o)}const g=e(t,[["render",c]]);export{m as __pageData,g as default};
