import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-842 Powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-842.md","filePath":"vacode/6.2-842.md"}'),i={name:"vacode/6.2-842.md"},o=r('<h1 id="§-6-2-842-powers" tabindex="-1">§ 6.2-842 Powers. <a class="header-anchor" href="#§-6-2-842-powers" aria-label="Permalink to &quot;§ 6.2-842 Powers.&quot;">​</a></h1><p>A. An out-of-state state bank that establishes and maintains one or more branches in the Commonwealth under this article may conduct the same activities at such branch or branches that are authorized under Virginia law for Virginia state banks, except to the extent such activities may be prohibited by other laws, regulations, or orders applicable to the out-of-state state bank.</p><p>B. A Virginia state bank may conduct the same activities at a branch outside the Commonwealth that are permissible for a bank chartered by the host state where the branch is located, except to the extent such activities are expressly prohibited by other laws, regulations, or orders applicable to the Virginia state bank.</p><p>C. A bank shall not establish or maintain a branch in the Commonwealth on the premises or property of an affiliate if the affiliate engages in commercial activities.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0301" target="_blank" rel="noreferrer">301</a>, § 6.1-44.8; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',5),s=[o];function n(c,h,l,p,b,d){return t(),a("div",null,s)}const m=e(i,[["render",n]]);export{f as __pageData,m as default};
