import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-872 For what purpose banks may purchase, hold, and convey real estate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-872.md","filePath":"vacode/6.2-872.md"}'),o={name:"vacode/6.2-872.md"},s=r('<h1 id="§-6-2-872-for-what-purpose-banks-may-purchase-hold-and-convey-real-estate" tabindex="-1">§ 6.2-872 For what purpose banks may purchase, hold, and convey real estate. <a class="header-anchor" href="#§-6-2-872-for-what-purpose-banks-may-purchase-hold-and-convey-real-estate" aria-label="Permalink to &quot;§ 6.2-872 For what purpose banks may purchase, hold, and convey real estate.&quot;">​</a></h1><p>A. In addition to the authority provided in § <a href="http://law.lis.virginia.gov/vacode/6.2-873/" target="_blank" rel="noreferrer">6.2-873</a>, every bank incorporated under the laws of the Commonwealth may purchase, hold, and convey the following real estate for the purposes stated and for no other:</p><ol><li><p>Real estate that is desirable and prudent for its present or future accommodation in the transaction of its business;</p></li><li><p>Real estate that is mortgaged or otherwise encumbered to it in good faith by way of security for debts contracted;</p></li><li><p>Real estate that is conveyed to it in satisfaction of debts previously contracted in the course of its dealings; and</p></li><li><p>Real estate it purchased at sales under judgments, decrees, mortgages, or deeds of trust held by it, in whole or in part, or purchased to secure debts due to it.</p></li></ol><p>B. Nothing in this section shall affect the validity of the title to any such real estate conveyed or transferred by a bank.</p><p>Code 1950, § 6-50; 1966, c. 584, § 6.1-59; 1988, c. 296; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0059" target="_blank" rel="noreferrer">59</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0157" target="_blank" rel="noreferrer">157</a>.</p>',5),n=[s];function i(l,d,c,h,p,f){return t(),a("div",null,n)}const g=e(o,[["render",i]]);export{_ as __pageData,g as default};
