import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-880 Construction loans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-880.md","filePath":"vacode/6.2-880.md"}'),o={name:"vacode/6.2-880.md"},r=n('<h1 id="§-6-2-880-construction-loans" tabindex="-1">§ 6.2-880 Construction loans. <a class="header-anchor" href="#§-6-2-880-construction-loans" aria-label="Permalink to &quot;§ 6.2-880 Construction loans.&quot;">​</a></h1><p>A. As used in this section, &quot;construction loan&quot; means a loan (i) made to finance the construction of a building or otherwise to improve real estate and (ii) with a maturity not exceeding 60 months.</p><p>B. A construction loan that is accompanied by a valid and binding agreement to advance an amount equal to or greater than the construction loan upon the completion of the building or improvement, which agreement is entered into by an individual or entity acceptable to the bank or the bank itself, whether or not secured by a mortgage or similar lien on the real estate upon which the building or improvement is being constructed, shall not be considered as a loan secured by real estate within the meaning of § <a href="http://law.lis.virginia.gov/vacode/6.2-878/" target="_blank" rel="noreferrer">6.2-878</a>, but shall be classed as an ordinary commercial loan, unless the terms of the transaction shall be more favorable than in the absence of a lien, in which case an appraisal shall be required as provided under § <a href="http://law.lis.virginia.gov/vacode/6.2-878/" target="_blank" rel="noreferrer">6.2-878</a>.</p><p>C. No bank shall invest in, or be liable in, construction loans in an aggregate amount in excess of 100 percent of its capital and surplus, except that any such loans supported by an executed agreement for permanent financing shall not be included in such aggregate amount.</p><p>D. Loans to finance construction of buildings or otherwise to improve real estate may be made under this section or under the provisions of § <a href="http://law.lis.virginia.gov/vacode/6.2-878/" target="_blank" rel="noreferrer">6.2-878</a>.</p><p>E. Loans made under subsection H of § <a href="http://law.lis.virginia.gov/vacode/6.2-878/" target="_blank" rel="noreferrer">6.2-878</a> or subsection A of § <a href="http://law.lis.virginia.gov/vacode/6.2-879/" target="_blank" rel="noreferrer">6.2-879</a> shall not be treated as construction loans for purposes of the limitations of this section.</p><p>Code 1950, § 6-78; 1952, c. 25; 1956, c. 622; 1960, c. 23; 1964, c. 150; 1966, c. 584, § 6.1-64; 1970, c. 14; 1972, c. 189; 1981, c. 271; 1991, c. 160; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',7),i=[r];function s(l,c,h,d,u,p){return a(),t("div",null,i)}const b=e(o,[["render",s]]);export{f as __pageData,b as default};
