import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 6.2-887 Insurance business of controlled subsidiary.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-887.md","filePath":"vacode/6.2-887.md"}'),o={name:"vacode/6.2-887.md"},r=n('<h1 id="§-6-2-887-insurance-business-of-controlled-subsidiary" tabindex="-1">§ 6.2-887 Insurance business of controlled subsidiary. <a class="header-anchor" href="#§-6-2-887-insurance-business-of-controlled-subsidiary" aria-label="Permalink to &quot;§ 6.2-887 Insurance business of controlled subsidiary.&quot;">​</a></h1><p>A. In addition to the types of business authorized in § <a href="http://law.lis.virginia.gov/vacode/6.2-885/" target="_blank" rel="noreferrer">6.2-885</a>, a controlled subsidiary corporation that is a domestic or foreign corporation, the majority of the voting stock of which is owned, directly or indirectly, by (i) a bank or banks organized under the laws of the United States, (ii) a bank or banks organized under the laws of the Commonwealth, (iii) a bank or banks organized under the laws of another state, or (iv) a bank holding company owning a bank or banks in the Commonwealth or in another state, may be formed to:</p><ol><li><p>Transact the type of insurance business specified in § <a href="http://law.lis.virginia.gov/vacode/38.2-120/" target="_blank" rel="noreferrer">38.2-120</a> and other insurance normally written under the coverage known as financial institution blanket bonds;</p></li><li><p>Underwrite insurance indemnifying the bank, its holding companies or its affiliates, and their directors and officers against liability; and</p></li><li><p>Underwrite reinsurance of mortgage guaranty insurance, subject to such conditions as the Commission may impose, on loans secured by real estate made or purchased by such controlled reinsurance subsidiary&#39;s affiliates or by a bank owning such controlled subsidiary.</p></li></ol><p>B. Any such controlled subsidiary corporation shall (i) transact only the insurance business specifically permitted by this section and (ii) be subject to the further provisions of Title 38.2 otherwise applicable to insurance companies transacting a comparable business.</p><p>C. The investment of any bank in the stock, services, or other obligations of such a controlled subsidiary shall not exceed two percent of such bank&#39;s capital, surplus, and undivided profits.</p><p>1976, c. 340, § 6.1-58.2; 1977, c. 190; 1986, c. 638; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0048" target="_blank" rel="noreferrer">48</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),t=[r];function s(c,l,d,u,h,b){return a(),i("div",null,t)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
