import{_ as r,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 6.2-919 Interest on deposits; distribution of surplus remaining after payment of depositors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-919.md","filePath":"vacode/6.2-919.md"}'),s={name:"vacode/6.2-919.md"},n=e("h1",{id:"§-6-2-919-interest-on-deposits-distribution-of-surplus-remaining-after-payment-of-depositors",tabindex:"-1"},[t("§ 6.2-919 Interest on deposits; distribution of surplus remaining after payment of depositors. "),e("a",{class:"header-anchor",href:"#§-6-2-919-interest-on-deposits-distribution-of-surplus-remaining-after-payment-of-depositors","aria-label":'Permalink to "§ 6.2-919 Interest on deposits; distribution of surplus remaining after payment of depositors."'},"​")],-1),i=e("p",null,"When an appropriate court, on a proper application therefor, shall appoint a receiver for any bank or trust company, the court may prescribe and direct, by order or decree entered of record, that the rate of interest to be paid by the receiver upon the claims of depositors of the bank or trust company shall not exceed the current or contracted rate of interest paid by the state bank or trust company on deposits. In addition, the court may fix the interest to be so paid at such lower rate as the court may deem proper under all the circumstances of the case. In such event, the court shall also direct that any surplus remaining after the payment in full of the depositors, together with the interest thereon as so prescribed and fixed, shall be distributed pro rata among the shareholders of the bank or trust company as of the date of the appointment of the receiver.",-1),d=e("p",null,[t("Code 1950, § 6-69; 1966, c. 584, § 6.1-105; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),t(".")],-1),p=[n,i,d];function c(h,l,f,u,m,b){return o(),a("div",null,p)}const g=r