import{_ as s,o as a,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 6.2-934 Receivership procedures involving assets held by closed bank as fiduciary.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-934.md","filePath":"vacode/6.2-934.md"}'),r={name:"vacode/6.2-934.md"},o=e("h1",{id:"§-6-2-934-receivership-procedures-involving-assets-held-by-closed-bank-as-fiduciary",tabindex:"-1"},[t("§ 6.2-934 Receivership procedures involving assets held by closed bank as fiduciary. "),e("a",{class:"header-anchor",href:"#§-6-2-934-receivership-procedures-involving-assets-held-by-closed-bank-as-fiduciary","aria-label":'Permalink to "§ 6.2-934 Receivership procedures involving assets held by closed bank as fiduciary."'},"​")],-1),n=e("p",null,"The FDIC as receiver, with the approval of the receivership court, has the authority to appoint a successor to all rights, obligations, assets, deposits, agreements, and trusts held by the closed bank as trustee, administrator, executor, guardian, agent, or in any other fiduciary or representative capacity. The successor's duties and obligations commence upon appointment and are to the same extent binding upon the former bank as though the successor had originally assumed such duties and obligations. Specifically, the successor shall succeed to and be entitled to administer all trusteeships, administrations, executorships, guardianships, agencies, and all other fiduciary or representative proceedings to which the closed bank is named or appointed in wills, whenever probated, or to which it is appointed by any other instrument, court order, or by operation of law. Nothing in this section shall be construed to impair any right of the grantor or beneficiary of trust assets to secure the appointment of a substitute trustee or manager. Within 30 days after appointment, the successor shall (i) give written notice, insofar as practicable, to all interested parties named in the books and records of the bank or in trust documents held by it that such successor has been appointed in accordance with state law and (ii) cause the fact of its appointment to be recorded in appropriate courts of record.",-1),c=e("p",null,[t("1983, c. 507, § 6.1-110.10; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),t(".")],-1),d=[o,n,c];function h(l,p,u,b,f,g){return a(),i("div",null,d)}const y=s(r,[["render",h]]);export{v as __pageData,y as default};
