import{_ as a,o,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 6.2-935 Termination of executory contracts and leases; liability; extension of statute of limitations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-935.md","filePath":"vacode/6.2-935.md"}'),n={name:"vacode/6.2-935.md"},s=e("h1",{id:"§-6-2-935-termination-of-executory-contracts-and-leases-liability-extension-of-statute-of-limitations",tabindex:"-1"},[t("§ 6.2-935 Termination of executory contracts and leases; liability; extension of statute of limitations. "),e("a",{class:"header-anchor",href:"#§-6-2-935-termination-of-executory-contracts-and-leases-liability-extension-of-statute-of-limitations","aria-label":'Permalink to "§ 6.2-935 Termination of executory contracts and leases; liability; extension of statute of limitations."'},"​")],-1),r=e("p",null,"Within 180 days of the date of the closing of the bank, the FDIC as receiver at its election may reject (i) any executory contract to which the closed bank is party without further liability to the closed bank or the receiver or (ii) any obligation of the bank as a lessee of real or personal property. The receiver's election to reject a lease creates no claim (a) for rent other than rent accrued to the date of termination or (b) for actual damages, if any, for such termination, not to exceed the equivalent of six months' payment. Notwithstanding any other law of the Commonwealth, the statute of limitations shall be extended for a period of six months on all causes of action which may accrue to the FDIC as receiver.",-1),c=e("p",null,[t("1983, c. 507, § 6.1-110.11; 2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794",target:"_blank",rel:"noreferrer"},"794"),t(".")],-1),l=[s,r,c];function f(h,d,m,u,y,_){return o(),i("div",null,l)}const b=a(n,[["render",f]]);export{x as __pageData,b as default};
