import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 6.2-941 Use of bank name, logo, or symbol for marketing purposes; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-941.md","filePath":"vacode/6.2-941.md"}'),n={name:"vacode/6.2-941.md"},r=t('<h1 id="§-6-2-941-use-of-bank-name-logo-or-symbol-for-marketing-purposes-penalty" tabindex="-1">§ 6.2-941 Use of bank name, logo, or symbol for marketing purposes; penalty. <a class="header-anchor" href="#§-6-2-941-use-of-bank-name-logo-or-symbol-for-marketing-purposes-penalty" aria-label="Permalink to &quot;§ 6.2-941 Use of bank name, logo, or symbol for marketing purposes; penalty.&quot;">​</a></h1><p>A. As used in this section, &quot;name, logo, or symbol, or any combination thereof, of a bank&quot; includes any name, logo, or symbol, or any combination thereof, that is deceptively similar to the name, logo, or symbol, or any combination thereof of a bank.</p><p>B. Except as provided in subsection C, no person shall use the name, logo, or symbol, or any combination thereof, of a bank in marketing material provided to or solicitation of another person in a manner such that a reasonable person may believe that the marketing material or solicitation originated from or is endorsed by the bank or that the bank is responsible for the marketing material or solicitation.</p><p>C. This section shall not apply to (i) an affiliate or agent of the bank or (ii) a person who uses the name, logo, or symbol of a bank with the consent of the bank.</p><p>D. Any person violating the provisions of this section, either individually or as an interested party, is guilty of a Class 1 misdemeanor. This section shall not affect the availability of any remedies otherwise available to a bank.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0240" target="_blank" rel="noreferrer">240</a>, § 6.1-119.1; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',6),i=[r];function s(l,p,m,h,c,f){return o(),a("div",null,i)}const g=e(n,[["render",s]]);export{d as __pageData,g as default};
