import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 6.2-948 Privilege for self-assessment reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/6.2-948.md","filePath":"vacode/6.2-948.md"}'),a={name:"vacode/6.2-948.md"},s=o('<h1 id="§-6-2-948-privilege-for-self-assessment-reports" tabindex="-1">§ 6.2-948 Privilege for self-assessment reports. <a class="header-anchor" href="#§-6-2-948-privilege-for-self-assessment-reports" aria-label="Permalink to &quot;§ 6.2-948 Privilege for self-assessment reports.&quot;">​</a></h1><p>Except as otherwise provided in this article:</p><ol><li><p>A self-assessment report and any portion or contents thereof are privileged and are not admissible or subject to discovery in any civil or administrative litigation, action, proceeding, or investigation;</p></li><li><p>The self-assessment privilege shall be applicable regardless of whether a bank regulator or any other governmental authority in possession of a self-assessment report or any portion or contents thereof subsequently discloses it or any portion or contents thereof to a third party (i) in accordance with subsection B of § <a href="http://law.lis.virginia.gov/vacode/6.2-101/" target="_blank" rel="noreferrer">6.2-101</a> or (ii) as required or permitted by any other state or federal law; and</p></li><li><p>Notwithstanding any state or federal law, a bank regulator or any other governmental authority in possession of a self-assessment report or any portion or contents thereof shall not disclose the report or any portion or contents thereof to a person in response to a request made pursuant to the Virginia Freedom of Information Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3700/" target="_blank" rel="noreferrer">2.2-3700</a> et seq.) or any similar federal or state public records law.</p></li></ol><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0032" target="_blank" rel="noreferrer">32</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',4),i=[s];function n(l,p,c,f,d,h){return r(),t("div",null,i)}const v=e(a,[["render",n]]);export{_ as __pageData,v as default};
