import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 60.2-113 Employment stabilization.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-113.md","filePath":"vacode/60.2-113.md"}'),t={name:"vacode/60.2-113.md"},n=r('<h1 id="§-60-2-113-employment-stabilization" tabindex="-1">§ 60.2-113 Employment stabilization. <a class="header-anchor" href="#§-60-2-113-employment-stabilization" aria-label="Permalink to &quot;§ 60.2-113 Employment stabilization.&quot;">​</a></h1><p>The Commission shall take all necessary steps through its appropriate divisions and with the advice of such advisory boards and committees as it may have to:</p><ol><li><p>Establish a viable labor exchange system to promote maximum employment for the Commonwealth of Virginia with priority given to those workers drawing unemployment benefits;</p></li><li><p>Provide Virginia State Job Service services, as described in this title, according to the provisions of the Wagner-Peyser Act (29 U.S.C. 49f), as amended by the Workforce Innovation and Opportunity Act;</p></li><li><p>Maintain a solvent trust fund financed through equitable employer taxes that provide temporary partial income replacement to involuntarily unemployed covered workers;</p></li><li><p>Coordinate and conduct labor market information research studies, programs and operations, including the development, storage, retrieval and dissemination of information on the social and economic aspects of the Commonwealth and publish data needed by employers, economic development, education and training entities, government and other users in the public and private sectors;</p></li><li><p>Encourage and assist in the adoption of practical methods of vocational guidance, training and retraining; and</p></li><li><p>Establish the Interagency Migrant Worker Policy Committee, comprised of representatives from appropriate state agencies, including the Virginia Workers&#39; Compensation Commission, whose services and jurisdictions involve migrant and seasonal farmworkers and their employees. All agencies of the Commonwealth shall be required to cooperate with the Committee upon request.</p></li></ol><p>Code 1950, § 60-34; 1968, c. 738, § 60.1-39; 1986, c. 480; 1989, c. 108; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0357" target="_blank" rel="noreferrer">357</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0014" target="_blank" rel="noreferrer">14</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0592" target="_blank" rel="noreferrer">592</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0098" target="_blank" rel="noreferrer">98</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0222" target="_blank" rel="noreferrer">222</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0594" target="_blank" rel="noreferrer">594</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0275" target="_blank" rel="noreferrer">275</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0292" target="_blank" rel="noreferrer">292</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0020" target="_blank" rel="noreferrer">20</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',4),o=[n];function l(s,c,p,g,h,d){return i(),a("div",null,o)}const v=e(t,[["render",l]]);export{f as __pageData,v as default};
