import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 60.2-119 Criminal cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-119.md","filePath":"vacode/60.2-119.md"}'),i={name:"vacode/60.2-119.md"},a=r('<h1 id="§-60-2-119-criminal-cases" tabindex="-1">§ 60.2-119 Criminal cases. <a class="header-anchor" href="#§-60-2-119-criminal-cases" aria-label="Permalink to &quot;§ 60.2-119 Criminal cases.&quot;">​</a></h1><p>All criminal actions for violation of any provision of this title, or of any rules or regulations issued pursuant to this title, shall be prosecuted by the attorney for the Commonwealth of the county or city in which the offense, or a part thereof, was committed, except that the offense set out in § <a href="http://law.lis.virginia.gov/vacode/60.2-518/" target="_blank" rel="noreferrer">60.2-518</a> or <a href="http://law.lis.virginia.gov/vacode/60.2-632/" target="_blank" rel="noreferrer">60.2-632</a> shall be deemed to be committed and venue for the prosecution shall lie in the county or city wherein the statement, representation, or nondisclosure is received by the Commission. However, if a defendant resides in this Commonwealth and the courthouse of the county or city in which he resides is more than 100 miles from the City of Richmond, venue for such prosecution shall lie in the city or county where he resides, and the offense shall be prosecuted by the attorney for the Commonwealth for the city or county where the defendant resides. If, in the opinion of the Commission, the prosecution should be conducted by the Office of the Attorney General, that office, upon the request of the Commission, shall have authority to conduct or supervise such prosecution.</p><p>Code 1950, § 60-110; 1968, c. 738, § 60.1-127; 1972, c. 764; 1980, c. 674; 1986, c. 480; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0105" target="_blank" rel="noreferrer">105</a>.</p>',3),n=[a];function s(c,h,l,d,f,u){return t(),o("div",null,n)}const _=e(i,[["render",s]]);export{p as __pageData,_ as default};
