import{_ as e,o as i,c as o,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 60.2-536 Review of decision under § 60.2-535.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-536.md","filePath":"vacode/60.2-536.md"}'),t={name:"vacode/60.2-536.md"},a=r('<h1 id="§-60-2-536-review-of-decision-under-§-60-2-535" tabindex="-1">§ 60.2-536 Review of decision under § 60.2-535. <a class="header-anchor" href="#§-60-2-536-review-of-decision-under-§-60-2-535" aria-label="Permalink to &quot;§ 60.2-536 Review of decision under § 60.2-535.&quot;">​</a></h1><p>A. Any person aggrieved by a decision of the Commission under the provisions of § <a href="http://law.lis.virginia.gov/vacode/60.2-535/" target="_blank" rel="noreferrer">60.2-535</a> shall have the right to review before the Commission. Such review before the Commission shall be instituted by a request filed by the aggrieved party with the Commission within thirty days from the date of mailing of the decision.</p><p>B. Any party aggrieved by the Commission decision on review may secure judicial review of any decision pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/60.2-500/" target="_blank" rel="noreferrer">60.2-500</a>, such provisions applying mutatis mutandis.</p><p>1974, c. 198, § 60.1-88.1; 1981, c. 252; 1986, c. 480; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>.</p>',4),n=[a];function s(d,c,h,l,_,f){return i(),o("div",null,n)}const m=e(t,[["render",s]]);export{v as __pageData,m as default};
