import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 60.2-537 Reduced tax rate permissible under federal amendment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-537.md","filePath":"vacode/60.2-537.md"}'),n={name:"vacode/60.2-537.md"},d=r('<h1 id="§-60-2-537-reduced-tax-rate-permissible-under-federal-amendment" tabindex="-1">§ 60.2-537 Reduced tax rate permissible under federal amendment. <a class="header-anchor" href="#§-60-2-537-reduced-tax-rate-permissible-under-federal-amendment" aria-label="Permalink to &quot;§ 60.2-537 Reduced tax rate permissible under federal amendment.&quot;">​</a></h1><p>Notwithstanding the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/60.2-500/" target="_blank" rel="noreferrer">60.2-500</a> through <a href="http://law.lis.virginia.gov/vacode/60.2-536/" target="_blank" rel="noreferrer">60.2-536</a>, if § 3303 of the Internal Revenue Code is amended so as to allow an additional credit under § 3302 (b) of the Internal Revenue Code to employers not otherwise eligible for a reduced rate by reason of the lapse of insufficient time since first becoming subject to this title, the Commission, by a regulation promulgated under § <a href="http://law.lis.virginia.gov/vacode/60.2-111/" target="_blank" rel="noreferrer">60.2-111</a>, shall fix and determine the tax rate of all such employers at such reduced rate as shall then be permissible under such federal amendment.</p><p>Code 1950, § 60-76.1; 1956, c. 440; 1968, c. 738, § 60.1-90; 1986, c. 480.</p>',3),o=[d];function i(s,l,c,m,h,f){return a(),t("div",null,o)}const p=e(n,[["render",i]]);export{_ as __pageData,p as default};
