import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 60.2-608.2 Withholding of benefits; food stamp overissuance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-608.2.md","filePath":"vacode/60.2-608.2.md"}'),n={name:"vacode/60.2-608.2.md"},a=i('<h1 id="§-60-2-608-2-withholding-of-benefits-food-stamp-overissuance" tabindex="-1">§ 60.2-608.2 Withholding of benefits; food stamp overissuance. <a class="header-anchor" href="#§-60-2-608-2-withholding-of-benefits-food-stamp-overissuance" aria-label="Permalink to &quot;§ 60.2-608.2 Withholding of benefits; food stamp overissuance.&quot;">​</a></h1><p>A. Any individual filing a new claim for unemployment compensation shall, at the time of filing such claim, disclose whether or not the individual owes an uncollected overissuance of food stamp coupons, as such is defined in § 13(c) (1) of the Food Stamp Act of 1977, 7 U.S.C. § 2022 (c) (1). If any such individual discloses that he or she owes food stamp obligations and is determined to be eligible for unemployment compensation, the Commission shall notify the state food stamp agency enforcing such obligation that the individual has been determined to be eligible for unemployment compensation.</p><p>B. The Commission shall deduct and withhold the following from any unemployment compensation payable to an individual who owes an uncollected overissuance:</p><ol><li><p>The amount specified by the individual to the Commission to be deducted and withheld under this subsection, if neither the provisions of subdivision 2 nor the provisions of subdivision 3 of this subsection are applicable; or</p></li><li><p>The amount, if any, determined pursuant to an agreement submitted to the Commission by the state food stamp agency under § 13(c) (3) (A) of the Food Stamp Act of 1977, 7 U.S.C. § 2022 (c) (3) (A); or</p></li><li><p>Any amount otherwise required to be so deducted and withheld from such unemployment compensation pursuant to § 13(c) (3) (B) of the Food Stamp Act of 1977, 7 U.S.C. § 2022 (c) (3) (B).</p></li></ol><p>C. Any amount deducted and withheld under subsection B shall be paid by the Commission to the appropriate state food stamp agency.</p><p>D. Any amount deducted and withheld under subsection B shall for all purposes be treated as if it were paid to the individual as unemployment compensation and paid by such individual to the state food stamp agency as repayment of the individual&#39;s uncollected overissuance.</p><p>E. For purposes of subsections A through D of this section, the term &quot;unemployment compensation&quot; means any compensation payable under this title including amounts payable by the Commission pursuant to an agreement under any federal law providing for compensation, assistance, or allowances with respect to unemployment.</p><p>F. The provisions of this section shall be applicable only if appropriate arrangements have been made for reimbursement by the state food stamp agency for the administrative costs incurred by the Commission under this subsection which are attributable to the repayment of uncollected overissuances to the state food stamp agency.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0385" target="_blank" rel="noreferrer">385</a>.</p>',9),s=[a];function d(r,l,c,h,p,m){return o(),t("div",null,s)}const b=e(n,[["render",d]]);export{f as __pageData,b as default};
