import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 60.2-613 Benefits not denied to individuals in training with approval of Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-613.md","filePath":"vacode/60.2-613.md"}'),a={name:"vacode/60.2-613.md"},n=t('<h1 id="§-60-2-613-benefits-not-denied-to-individuals-in-training-with-approval-of-commission" tabindex="-1">§ 60.2-613 Benefits not denied to individuals in training with approval of Commission. <a class="header-anchor" href="#§-60-2-613-benefits-not-denied-to-individuals-in-training-with-approval-of-commission" aria-label="Permalink to &quot;§ 60.2-613 Benefits not denied to individuals in training with approval of Commission.&quot;">​</a></h1><p>A. No otherwise eligible individual shall be denied benefits for any week because he is in training with the approval of the Commission, including training under § 134 of the Workforce Investment Act, nor shall such individual be denied benefits for any week in which he is in training with the approval of the Commission, including training under § 134 of the Workforce Investment Act, by reason of the application of the provisions in subdivision A 7 of § <a href="/vacode/60.2-612/">60.2-612</a> relating to availability for work, or the provisions of subdivision 3 of § <a href="/vacode/60.2-618/">60.2-618</a> relating to failure to apply for, or a refusal to accept, suitable work.</p><p>B. Notwithstanding any other provisions of this chapter, no otherwise eligible individual shall be denied benefits for any week because he is in training approved under § 2296 of the Trade Act (19 U.S.C. § 2101 et seq.), nor shall such individual be denied benefits by reason of leaving work to enter such training, provided the work left is not suitable employment, or because of the application to any such week in training of provisions in this law (or any applicable federal unemployment compensation law), relating to availability for work, active search for work, or refusal to accept work.</p><p>C. For purposes of this section, &quot;suitable employment&quot; means, with respect to an individual, work of a substantially equal or higher skill level than the individual&#39;s past adversely affected employment, as defined for purposes of the Trade Act, and wages for such work at not less than eighty percent of the individual&#39;s average weekly wage as determined for the purposes of the Trade Act.</p><p>1971, Ex. Sess., c. 235, § 60.1-52.1; 1982, c. 237; 1984, c. 204; 1985, c. 152; 1986, c. 480; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0687" target="_blank" rel="noreferrer">687</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>.</p>',5),r=[n];function s(l,d,f,c,h,p){return i(),o("div",null,r)}const g=e(a,[["render",s]]);export{v as __pageData,g as default};
