import{_ as a,o as s,c as t,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 60.2-621 Appeal tribunals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/60.2-621.md","filePath":"vacode/60.2-621.md"}'),n={name:"vacode/60.2-621.md"},i=e("h1",{id:"§-60-2-621-appeal-tribunals",tabindex:"-1"},[o("§ 60.2-621 Appeal tribunals. "),e("a",{class:"header-anchor",href:"#§-60-2-621-appeal-tribunals","aria-label":'Permalink to "§ 60.2-621 Appeal tribunals."'},"​")],-1),r=e("p",null,"In order to hear and decide disputed claims expeditiously, the Commissioner shall establish one or more impartial appeal tribunals consisting in each case of either (i) a salaried examiner or (ii) a tribunal consisting of three members, one of whom shall be a salaried examiner, who shall serve as chairman, one of whom shall be a representative of employers and the other of whom shall be a representative of employees. Each of the latter two members of the tribunal in (ii) of this section shall serve at the pleasure of the Commissioner and be paid a fee of not more than ten dollars per day of active service on such tribunal plus necessary expenses. No person shall participate on behalf of the Commission in any case in which he is an interested party. The Commissioner may designate alternates to serve in the absence or disqualification of any member of an appeal tribunal. The chairman shall act alone in the absence or disqualification of any other member and his alternates. In no case shall the hearings proceed unless the chairman of the appeal tribunal is present.",-1),l=e("p",null,"Code 1950, § 60-51; 1968, c. 738, § 60.1-63; 1986, c. 480.",-1),h=[i,r,l];function c(p,d,m,f,u,b){return s(),t("div",null,h)}const y=a(n,[["render",c]]);export{v as __pageData,y as default};
