import{_ as a,o as s,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 61.1-38 Establishment and discontinuance of warehouses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/61.1-38.md","filePath":"vacode/61.1-38.md"}'),n={name:"vacode/61.1-38.md"},i=e("h1",{id:"§-61-1-38-establishment-and-discontinuance-of-warehouses",tabindex:"-1"},[t("§ 61.1-38 Establishment and discontinuance of warehouses. "),e("a",{class:"header-anchor",href:"#§-61-1-38-establishment-and-discontinuance-of-warehouses","aria-label":'Permalink to "§ 61.1-38 Establishment and discontinuance of warehouses."'},"​")],-1),c=e("p",null,"Tobacco warehouses, which were public warehouses on the day before this Code takes effect, shall continue to be such; and the several circuit and corporation courts may hereafter authorize the erection of tobacco warehouses, or may establish the same, as public warehouses within their respective counties and cities; which warehouses shall be constructed, or shall have been constructed, so as to keep safely, and guard against fire and weather, as far as practicable, all tobacco stored therein, and shall be kept in good repair and at all times, Sunday excepted, be open for receiving, storing, selling and delivering tobacco; but the owner of any such warehouse shall have the right to discontinue the same as a public warehouse, after having published a notice of his intention to do so once a week for four successive weeks in some newspaper published in the county or city, wherein such warehouse is situated, or if no newspaper be published therein, after having posted such notice at the front door of the courthouse of such county or city for four successive weeks.",-1),r=e("p",null,"Code 1950, § 61-95; 1968, c. 69.",-1),h=[i,c,r];function d(u,l,f,p,b,w){return s(),o("div",null,h)}const v=a(n,[["render",d]]);export{_ as __pageData,v as default};
