import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 62.1-132.1 General powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-132.1.md","filePath":"vacode/62.1-132.1.md"}'),i={name:"vacode/62.1-132.1.md"},o=a('<h1 id="§-62-1-132-1-general-powers" tabindex="-1">§ 62.1-132.1 General powers. <a class="header-anchor" href="#§-62-1-132-1-general-powers" aria-label="Permalink to &quot;§ 62.1-132.1 General powers.&quot;">​</a></h1><p>A. Except as provided in subsection B, the Authority is vested with the powers of a body corporate, including, without limitation, to:</p><ol><li><p>Sue and be sued;</p></li><li><p>Make contracts;</p></li><li><p>Adopt and use a common seal, and alter such seal at its pleasure;</p></li><li><p>Procure insurance, participate in insurance plans, and provide self-insurance. The purchase of insurance, participation in an insurance plan, or the creation of a self-insurance plan by the Authority shall not be deemed a waiver or relinquishment of any sovereign immunity to which the Authority or its officers, directors, employees, or agents are otherwise entitled;</p></li><li><p>Develop policies and procedures generally applicable to the procurement of goods, services and construction based on competitive principles; and</p></li><li><p>Exercise all the powers that are conferred upon industrial development authorities created pursuant to Chapter 49 (§ <a href="/vacode/15.2-4900/">15.2-4900</a> et seq.) of Title 15.2, except that the power to effect a change in ownership or operation of the Port of Virginia shall be subject to the provisions of § <a href="/vacode/62.1-132.19/">62.1-132.19</a>.</p></li></ol><p>B. Expenditures by the Authority for capital projects are restricted to projects located on real property that is owned, leased, or operated by the Virginia Port Authority, except those expenditures (i) as provided in § <a href="/vacode/62.1-132.13/">62.1-132.13</a> or <a href="/vacode/62.1-132.14/">62.1-132.14</a>, (ii) on grants to local government for financial assistance for port facilities as approved by the Board in policies posted on the Authority&#39;s website, or (iii) to provide support for the types of projects eligible for funding under subsection A of § <a href="/vacode/33.2-1509/">33.2-1509</a>, subsection A of § <a href="/vacode/33.2-1600/">33.2-1600</a>, or § <a href="/vacode/33.2-1526.4/">33.2-1526.4</a>.</p><p>1981, c. 589; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0232" target="_blank" rel="noreferrer">232</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0609" target="_blank" rel="noreferrer">609</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',5),n=[o];function s(l,p,c,h,d,f){return r(),t("div",null,n)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
