import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 62.1-136 Power of eminent domain.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-136.md","filePath":"vacode/62.1-136.md"}'),i={name:"vacode/62.1-136.md"},a=r('<h1 id="§-62-1-136-power-of-eminent-domain" tabindex="-1">§ 62.1-136 Power of eminent domain. <a class="header-anchor" href="#§-62-1-136-power-of-eminent-domain" aria-label="Permalink to &quot;§ 62.1-136 Power of eminent domain.&quot;">​</a></h1><p>The Authority is hereby vested with the power of eminent domain to acquire property or any interest therein, however held, but not property of the Commonwealth or its agencies, and may exercise the same for the purposes set forth in §§ <a href="http://law.lis.virginia.gov/vacode/62.1-132.18/" target="_blank" rel="noreferrer">62.1-132.18</a> and <a href="http://law.lis.virginia.gov/vacode/62.1-132.19/" target="_blank" rel="noreferrer">62.1-132.19</a> in the manner and to the extent set forth in, and subject to the provisions of, Chapter 2 (§ <a href="http://law.lis.virginia.gov/vacode/25.1-200/" target="_blank" rel="noreferrer">25.1-200</a> et seq.) of Title 25.1; provided that the Authority shall have no power to condemn any property belonging to any other political subdivision of the Commonwealth, or to any common carrier, or public utility or other public service corporation which is being devoted to public use or service. Whether property is being devoted to public use or service in the case of a public service corporation, common carrier, or public utility, shall be decided by the State Corporation Commission in a proceeding under § <a href="http://law.lis.virginia.gov/vacode/25.1-102/" target="_blank" rel="noreferrer">25.1-102</a>; and in the case of a political subdivision shall be decided by the court in which the proceeding is brought.</p><p>Code 1950, § 62-106.8:1; 1962, c. 346; 1968, c. 659; 1981, c. 589; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>.</p>',3),n=[a];function c(s,h,l,d,p,m){return o(),t("div",null,n)}const v=e(i,[["render",c]]);export{_ as __pageData,v as default};
