import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 62.1-139 Forms of accounts and records; annual report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-139.md","filePath":"vacode/62.1-139.md"}'),n={name:"vacode/62.1-139.md"},o=r('<h1 id="§-62-1-139-forms-of-accounts-and-records-annual-report" tabindex="-1">§ 62.1-139 Forms of accounts and records; annual report. <a class="header-anchor" href="#§-62-1-139-forms-of-accounts-and-records-annual-report" aria-label="Permalink to &quot;§ 62.1-139 Forms of accounts and records; annual report.&quot;">​</a></h1><p>A. The accounts and records of the Authority showing the receipt and disbursement of funds from whatever source derived shall be in a form prescribed by governmental generally accepted accounting principles. Such accounts shall correspond as nearly as possible to the accounts and records for such matters maintained by enterprises.</p><p>B. The accounts of the Authority shall be audited annually by a certified public accounting firm selected by the Auditor of Public Accounts with the assistance of the Authority through a process of competitive negotiation. The cost of such audit and review shall be borne by the Authority.</p><p>C. The General Assembly may request the Auditor of Public Accounts to conduct audits at any time.</p><p>D. The Authority shall submit an annual report to the Governor and General Assembly on or before November 1 of each year. Such report shall contain the audited annual financial statements of the Authority for the year ending the preceding June 30.</p><p>E. The Authority shall submit a detailed annual operating plan and budget to the Secretary of Transportation and the Director of the Department of Planning and Budget by November 1 of each year. Notwithstanding any other provision of this chapter, the form and content of the operating plan and budget shall be determined by the Director of the Department of Planning and Budget and shall include information on salaries, expenditures, indebtedness, and other information as determined by the Director of the Department of Planning and Budget.</p><p>F. The Board of Commissioners, the General Assembly, or the Governor may at any time request that the Office of the Inspector General, created pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-308/" target="_blank" rel="noreferrer">2.2-308</a>, review any area of the Authority&#39;s finances or operations.</p><p>Code 1950, § 62-106.11; 1954, c. 667; 1968, c. 659; 1984, c. 734; 1985, c. 146; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0849" target="_blank" rel="noreferrer">849</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',8),i=[o];function s(c,l,h,d,u,f){return t(),a("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
