import{_ as o,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 62.1-202 Appointment and duties of Executive Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-202.md","filePath":"vacode/62.1-202.md"}'),i={name:"vacode/62.1-202.md"},n=e("h1",{id:"§-62-1-202-appointment-and-duties-of-executive-director",tabindex:"-1"},[t("§ 62.1-202 Appointment and duties of Executive Director. "),e("a",{class:"header-anchor",href:"#§-62-1-202-appointment-and-duties-of-executive-director","aria-label":'Permalink to "§ 62.1-202 Appointment and duties of Executive Director."'},"​")],-1),s=e("p",null,"The Governor shall appoint an Executive Director of the Authority, who shall report to, but not be a member of, the Board of Directors. The Executive Director shall serve as the ex officio secretary of the Board of Directors and shall administer, manage and direct the affairs and activities of the Authority in accordance with the policies and under the control and direction of the Board of Directors. He shall attend meetings of the Board of Directors, shall keep a record of the proceedings of the Board of Directors and shall maintain and be custodian of all books, documents and papers of the Authority, the minute book of the Authority and its official seal. He may cause copies to be made of all minutes and other records and documents of the Authority and may give certificates under seal of the Authority to the effect that the copies are true copies, and all persons dealing with the Authority may rely upon the certificates. He shall also perform other duties as instructed by the Board of Directors in carrying out the purposes of this chapter. He shall execute a surety bond in a penalty sum determined by the Board of Directors. The surety bond shall be executed by a surety company authorized to transact business in the Commonwealth and shall be conditioned upon the faithful performance of the duties of the office.",-1),c=e("p",null,[t("1984, c. 699; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0684",target:"_blank",rel:"noreferrer"},"684"),t(".")],-1),d=[n,s,c];function h(l,f,u,p,m,_){return a(),r("div",null,d)}const v=o(i,[["render",h]]);export{b as __pageData,v as default};
