import{_ as a,o as r,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 62.1-245 Agreements among persons withdrawing surface water.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-245.md","filePath":"vacode/62.1-245.md"}'),i={name:"vacode/62.1-245.md"},o=e("h1",{id:"§-62-1-245-agreements-among-persons-withdrawing-surface-water",tabindex:"-1"},[t("§ 62.1-245 Agreements among persons withdrawing surface water. "),e("a",{class:"header-anchor",href:"#§-62-1-245-agreements-among-persons-withdrawing-surface-water","aria-label":'Permalink to "§ 62.1-245 Agreements among persons withdrawing surface water."'},"​")],-1),s=e("p",null,[t("In the administration of this chapter, the Board shall encourage, promote and recognize voluntary agreements among persons withdrawing surface water in the same surface water management area. When the Board finds that any such agreement, executed in writing and filed with the Board, is consistent with the intent, purposes and requirements of this chapter, the Board shall approve the agreement following a public hearing. The Board shall provide at least sixty days' notice of the public hearing to the public in general and individually to those persons withdrawing surface water in the surface water management area who are not parties to the agreement, and shall make a good faith effort to so notify recreational user groups, conservation organizations and fisheries management agencies. The Board shall be a party to the agreement. The agreement, until terminated, shall control in lieu of a formal order, rule, regulation or permit issued by the Board under the provisions of this chapter, and shall be deemed to be a case decision under the Administrative Process Act (§ "),e("a",{href:"http://law.lis.virginia.gov/vacode/2.2-4000/",target:"_blank",rel:"noreferrer"},"2.2-4000"),t(" et seq.). Any agreement shall specify the amount of water affected thereby.")],-1),h=e("p",null,"Any agreement approved by the Board may include conditions which can result in its amendment or termination by the Board, following a public hearing, if the Board finds that it or its effect is inconsistent with the intent, purposes and requirements of this chapter. Such conditions may include (i) a determination by the Board that the agreement originally approved by the Board will not further the purposes of this chapter, (ii) a determination by the Board that circumstances have changed such that the agreement originally approved by the Board will no longer further the purposes by this chapter, or (iii) one or more parties to the agreement is not fulfilling its commitments under the agreement. The Board shall provide at least sixty days' notice of the public hearing to the public in general and individually to those persons withdrawing surface water in the surface water management area who are not parties to the agreement, and shall make a good faith effort to so notify recreational user groups, conservation organizations and fisheries management agencies.",-1),d=e("p",null,[t("1989, c. 721; 1999, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0561",target:"_blank",rel:"noreferrer"},"561"),t(".")],-1),l=[o,s,h,d];function c(g,m,p,f,u,w){return r(),n("div",null,l)}const v=a(i,[["render",c]]);export{_ as __pageData,v as default};
