import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 62.1-258 Use of ground water in ground water management area; registration of well construction required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-258.md","filePath":"vacode/62.1-258.md"}'),o={name:"vacode/62.1-258.md"},n=t('<h1 id="§-62-1-258-use-of-ground-water-in-ground-water-management-area-registration-of-well-construction-required" tabindex="-1">§ 62.1-258 Use of ground water in ground water management area; registration of well construction required. <a class="header-anchor" href="#§-62-1-258-use-of-ground-water-in-ground-water-management-area-registration-of-well-construction-required" aria-label="Permalink to &quot;§ 62.1-258 Use of ground water in ground water management area; registration of well construction required.&quot;">​</a></h1><p>It is unlawful in a ground water management area for any person to withdraw, attempt to withdraw, or allow the withdrawal of any ground water, other than in accordance with a ground water withdrawal permit or as provided in § <a href="http://law.lis.virginia.gov/vacode/62.1-259/" target="_blank" rel="noreferrer">62.1-259</a>, subsections C, D and F of § <a href="http://law.lis.virginia.gov/vacode/62.1-260/" target="_blank" rel="noreferrer">62.1-260</a>, and subsection C of § <a href="http://law.lis.virginia.gov/vacode/62.1-261/" target="_blank" rel="noreferrer">62.1-261</a>. Each private well, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-176.3/" target="_blank" rel="noreferrer">32.1-176.3</a>, constructed in a ground water management area shall be registered by the certified water well systems provider with the Board within 30 days of the completion of the construction. Such registration shall be in a format prescribed by the Board; however, the Board and the Board of Health shall develop joint private well forms and processes. The Department of Health shall provide the Board annually with a list of private wells that have received permits during the previous year. The list shall include each well&#39;s characteristics and location. The Board shall provide the Department of Health annually with a list of wells registered during the previous year.</p><p>1992, c. 812; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0465" target="_blank" rel="noreferrer">465</a>.</p>',3),i=[n];function l(s,d,c,h,g,u){return a(),r("div",null,i)}const p=e(o,[["render",l]]);export{w as __pageData,p as default};
