import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 62.1-44.15:27.1 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Virginia Stormwater Management Programs administered by the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.15:27.1.md","filePath":"vacode/62.1-44.15:27.1.md"}'),n={name:"vacode/62.1-44.15:27.1.md"},i=r('<h1 id="§-62-1-44-15-27-1-for-contingent-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-virginia-stormwater-management-programs-administered-by-the-board" tabindex="-1">§ 62.1-44.15:27.1 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Virginia Stormwater Management Programs administered by the Board. <a class="header-anchor" href="#§-62-1-44-15-27-1-for-contingent-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-virginia-stormwater-management-programs-administered-by-the-board" aria-label="Permalink to &quot;§ 62.1-44.15:27.1 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Virginia Stormwater Management Programs administered by the Board.&quot;">​</a></h1><p>A. The Board shall administer a Virginia Stormwater Management Program (VSMP) on behalf of any locality that notifies the Department pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/62.1-44.15:27/" target="_blank" rel="noreferrer">62.1-44.15:27</a> that it has chosen to not administer a VESMP as provided by subdivision B 3 of § <a href="http://law.lis.virginia.gov/vacode/62.1-44.15:27/" target="_blank" rel="noreferrer">62.1-44.15:27</a>. In such a locality:</p><ol><li><p>The Board shall implement a VSMP in order to manage the quality and quantity of stormwater runoff resulting from any land-disturbing activity that (i) disturbs one acre or more of land or (ii) disturbs less than one acre of land and is part of a larger common plan of development or sale that results in one acre or greater of land disturbance, as required by this article.</p></li><li><p>No person shall conduct a land-disturbing activity until he has obtained land-disturbance approval from the VESCP authority and, if required, submitted to the Department an application that includes a permit registration statement and stormwater management plan, and the Department has issued permit coverage.</p></li></ol><p>B. The Board shall adopt regulations establishing specifications for the VSMP, including permit requirements and requirements for plan review, inspection, and enforcement that reflect the analogous stormwater management requirements for a VESMP set forth in applicable provisions of this article.</p><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',5),o=[i];function s(d,c,l,m,h,f){return a(),t("div",null,o)}const u=e(n,[["render",s]]);export{p as __pageData,u as default};
