import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 62.1-44.15:30 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Training and certification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.15:30.md","filePath":"vacode/62.1-44.15:30.md"}'),i={name:"vacode/62.1-44.15:30.md"},n=r('<h1 id="§-62-1-44-15-30-for-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-training-and-certification" tabindex="-1">§ 62.1-44.15:30 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Training and certification. <a class="header-anchor" href="#§-62-1-44-15-30-for-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-training-and-certification" aria-label="Permalink to &quot;§ 62.1-44.15:30 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Training and certification.&quot;">​</a></h1><p>A. The Board shall issue separate or combined certifications concerning specified subject areas of this article, including program administration, plan review, and project inspection, to persons who have demonstrated adequate knowledge to the satisfaction of the Board. The Board also shall issue a Responsible Land Disturber certificate to personnel and contractors who have demonstrated adequate knowledge to the satisfaction of the Board.</p><p>B. The Department shall administer education and training programs for specified subject areas of this article and is authorized to charge persons attending such programs reasonable fees to cover the costs of administering the programs.</p><p>C. Personnel of VSMP or VESMP authorities who are administering programs, reviewing plans, or conducting inspections pursuant to this article shall hold a certification in the appropriate subject area as provided in subsection A. This requirement shall not apply to third-party individuals who prepare and submit plans to a VESMP or VSMP authority.</p><p>D. The Department shall establish procedures and requirements for issuance and periodic renewal of certifications.</p><p>E. Professionals registered in the Commonwealth pursuant to Article 1 (§ <a href="http://law.lis.virginia.gov/vacode/54.1-400/" target="_blank" rel="noreferrer">54.1-400</a> et seq.) of Chapter 4 of Title 54.1 shall be deemed to have met the provisions of this section for the purposes of renewals of such certifications.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0785" target="_blank" rel="noreferrer">785</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>, § 10.1-603.4:2; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',7),o=[n];function s(c,d,l,p,f,h){return a(),t("div",null,o)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
