import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 62.1-44.15:69 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.15:69.md","filePath":"vacode/62.1-44.15:69.md"}'),n={name:"vacode/62.1-44.15:69.md"},r=i('<h1 id="§-62-1-44-15-69-for-contingent-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-powers-and-duties-of-the-board" tabindex="-1">§ 62.1-44.15:69 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Powers and duties of the Board. <a class="header-anchor" href="#§-62-1-44-15-69-for-contingent-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 62.1-44.15:69 (For contingent effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Powers and duties of the Board.&quot;">​</a></h1><p>The Board is responsible for carrying out the purposes and provisions of this article and is authorized to:</p><ol><li><p>Provide land use and development and water quality protection information and assistance to the various levels of local, regional, and state government within the Commonwealth.</p></li><li><p>Consult, advise, and coordinate with the Governor, the Secretary, the General Assembly, other state agencies, regional agencies, local governments, and federal agencies for the purpose of implementing this article.</p></li><li><p>Provide financial and technical assistance and advice to local governments and to regional and state agencies concerning aspects of land use and development and water quality protection pursuant to this article.</p></li><li><p>Promulgate regulations pursuant to the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.).</p></li><li><p>Develop, promulgate, and keep current the criteria required by § <a href="http://law.lis.virginia.gov/vacode/62.1-44.15:72/" target="_blank" rel="noreferrer">62.1-44.15:72</a>.</p></li><li><p>Provide technical assistance and advice or other aid for the development, adoption, and implementation of local comprehensive plans, zoning ordinances, subdivision ordinances, and other land use and development and water quality protection measures utilizing criteria established by the Board to carry out the provisions of this article.</p></li><li><p>Develop procedures for use by local governments to designate Chesapeake Bay Preservation Areas in accordance with the criteria developed pursuant to § <a href="http://law.lis.virginia.gov/vacode/62.1-44.15:72/" target="_blank" rel="noreferrer">62.1-44.15:72</a>.</p></li><li><p>Ensure that local government comprehensive plans, zoning ordinances, and subdivision ordinances are in accordance with the provisions of this article. Determination of compliance shall be in accordance with the provisions of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.).</p></li><li><p>Make application for federal funds that may become available under federal acts and to transmit such funds when applicable to any appropriate person.</p></li><li><p>Take administrative and legal actions pursuant to subdivision (19) of § <a href="http://law.lis.virginia.gov/vacode/62.1-44.15/" target="_blank" rel="noreferrer">62.1-44.15</a> to ensure compliance by counties, cities, and towns with the provisions of this article including the proper enforcement and implementation of, and continual compliance with, this article.</p></li><li><p>Perform such other duties and responsibilities related to the use and development of land and the protection of water quality as the Secretary may assign.</p></li></ol><p>1988, cc. 608, 891, § 10.1-2103; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',4),o=[r];function s(l,c,d,p,h,f){return a(),t("div",null,o)}const u=e(n,[["render",s]]);export{g as __pageData,u as default};
