import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 62.1-44.17:3 Toxic substances reduction in state waters; report required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.17:3.md","filePath":"vacode/62.1-44.17:3.md"}'),o={name:"vacode/62.1-44.17:3.md"},r=a('<h1 id="§-62-1-44-17-3-toxic-substances-reduction-in-state-waters-report-required" tabindex="-1">§ 62.1-44.17:3 Toxic substances reduction in state waters; report required. <a class="header-anchor" href="#§-62-1-44-17-3-toxic-substances-reduction-in-state-waters-report-required" aria-label="Permalink to &quot;§ 62.1-44.17:3 Toxic substances reduction in state waters; report required.&quot;">​</a></h1><p>A. The Board shall (i) conduct ongoing assessments of the amounts of toxics in Virginia&#39;s waters and (ii) develop and implement a plan for the reduction of toxics in Virginia&#39;s waters.</p><p>B. The status of the Board&#39;s efforts to reduce the level of toxic substances in state waters shall be reported biennially, no later than January 1 in each odd-numbered year, to the House Committee on Agriculture, Chesapeake and Natural Resources and the Senate Committee on Agriculture, Conservation and Natural Resources. The report shall include the following information:</p><ol><li><p>Compliance data on permits that have limits for toxics;</p></li><li><p>The number of new permits or reissued permits that have toxic limits and the location of each permitted facility;</p></li><li><p>The location and number of monitoring stations and the period of time that monitoring has occurred at each location;</p></li><li><p>A summary of pollution prevention and pollution control activities for the reduction of toxics in state waters;</p></li><li><p>The sampling results from the monitoring stations for the previous two years;</p></li><li><p>The Board&#39;s plan for continued reduction of the discharge of toxics, which shall include, but not be limited to, additional monitoring activities, a work plan for the pollution prevention program, and any pilot projects established for the use of innovative technologies to reduce the discharge of toxics;</p></li><li><p>The identification of any segments for which the Board or the Director of the Department of Environmental Quality has made a decision to conduct additional evaluation or monitoring. Information regarding these segments shall include, at a minimum, the geographic location of the stream segment within a named county or city; and</p></li><li><p>The identification of any segments that are designated as toxic impaired waters as defined in § <a href="http://law.lis.virginia.gov/vacode/62.1-44.19:4/" target="_blank" rel="noreferrer">62.1-44.19:4</a> and any plans to address the impairment.</p></li></ol><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0017" target="_blank" rel="noreferrer">17</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1043" target="_blank" rel="noreferrer">1043</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0173" target="_blank" rel="noreferrer">173</a>.</p>',5),n=[r];function s(l,c,d,h,p,u){return t(),i("div",null,n)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
