import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 62.1-44.19:16 Technology-based standards and effluent limitations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.19:16.md","filePath":"vacode/62.1-44.19:16.md"}'),o={name:"vacode/62.1-44.19:16.md"},i=n('<h1 id="§-62-1-44-19-16-technology-based-standards-and-effluent-limitations" tabindex="-1">§ 62.1-44.19:16 Technology-based standards and effluent limitations. <a class="header-anchor" href="#§-62-1-44-19-16-technology-based-standards-and-effluent-limitations" aria-label="Permalink to &quot;§ 62.1-44.19:16 Technology-based standards and effluent limitations.&quot;">​</a></h1><p>A. The Board may establish a technology-based standard less stringent than the applicable standard specified in § <a href="http://law.lis.virginia.gov/vacode/62.1-44.19:15/" target="_blank" rel="noreferrer">62.1-44.19:15</a> based on a demonstration by an owner or operator that the specified standard is not technically or economically feasible for the affected facility or that the technology-based standard would require the owner or operator to construct treatment facilities not otherwise necessary to comply with his waste load allocation without reliance on nutrient credit exchanges pursuant to § <a href="http://law.lis.virginia.gov/vacode/62.1-44.19:18/" target="_blank" rel="noreferrer">62.1-44.19:18</a>.</p><p>B. The Board may include technology-based effluent concentration limitations in the individual permit for any facility that has installed technology for the control of nitrogen and phosphorus whether by new construction, expansion, or upgrade. Such limitations shall be based upon the technology installed by the facility and shall be expressed as annual average limitations. Such limitations shall not affect the generation, acquisition, or exchange of allocations or credits pursuant to this article.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0710" target="_blank" rel="noreferrer">710</a>.</p>',4),r=[i];function s(l,c,d,h,f,p){return a(),t("div",null,r)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
