import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 62.1-44.19:2 Additional requirements on sewage discharge in the Cities of Chesapeake, Hampton, Newport News, Norfolk and Virginia Beach.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.19:2.md","filePath":"vacode/62.1-44.19:2.md"}'),o={name:"vacode/62.1-44.19:2.md"},n=t('<h1 id="§-62-1-44-19-2-additional-requirements-on-sewage-discharge-in-the-cities-of-chesapeake-hampton-newport-news-norfolk-and-virginia-beach" tabindex="-1">§ 62.1-44.19:2 Additional requirements on sewage discharge in the Cities of Chesapeake, Hampton, Newport News, Norfolk and Virginia Beach. <a class="header-anchor" href="#§-62-1-44-19-2-additional-requirements-on-sewage-discharge-in-the-cities-of-chesapeake-hampton-newport-news-norfolk-and-virginia-beach" aria-label="Permalink to &quot;§ 62.1-44.19:2 Additional requirements on sewage discharge in the Cities of Chesapeake, Hampton, Newport News, Norfolk and Virginia Beach.&quot;">​</a></h1><p>A. Beginning January 1, 1973, every sewage pumping station in the Cities of Chesapeake, Hampton, Newport News, Norfolk, and Virginia Beach shall:</p><ol><li><p>Have adequate personnel on call at all times, each of whom may serve multiple pumping stations, as prescribed by the Chesapeake, Hampton, Newport News, Norfolk, or Virginia Beach City Council, respectively;</p></li><li><p>As prescribed by the Board, be inspected at such intervals and maintain such records of inspection. Such records shall be open for review by the Board or its representatives at any reasonable time it designates;</p></li><li><p>Have an automatic alarm system installed to give immediate warning of any pump station failure;</p></li><li><p>Have emergency pump connections installed and have portable pumps available to pump sewage to downstream sewer lines during any period of pump station failure;</p></li><li><p>Not use, except in an emergency pursuant to regulations as provided by the Board, any overflow line from any such pumping station except as provided in subdivision 4.</p></li></ol><p>B. Any sewerage system within the City of Chesapeake, Hampton, Newport News, Norfolk, or Virginia Beach that complies with the requirements of this section is deemed to meet the requirements for continuous operability as set forth in regulations of the Board or the State Department of Health.</p><p>1972, c. 840; 1975, c. 373; 1976, c. 188; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0235" target="_blank" rel="noreferrer">235</a>.</p>',5),r=[n];function s(p,l,c,d,h,m){return a(),i("div",null,r)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
