import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 62.1-44.19:3.4 Notification of local governing bodies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.19:3.4.md","filePath":"vacode/62.1-44.19:3.4.md"}'),i={name:"vacode/62.1-44.19:3.4.md"},n=o('<h1 id="§-62-1-44-19-3-4-notification-of-local-governing-bodies" tabindex="-1">§ 62.1-44.19:3.4 Notification of local governing bodies. <a class="header-anchor" href="#§-62-1-44-19-3-4-notification-of-local-governing-bodies" aria-label="Permalink to &quot;§ 62.1-44.19:3.4 Notification of local governing bodies.&quot;">​</a></h1><p>A. Whenever the Department receives an application for land disposal of treated sewage, stabilized sewage sludge, or stabilized septage, the Department shall notify the local governing bodies where disposal is to take place of pertinent details of the proposal and establish a date for a public meeting to discuss technical issues relating to the proposal. The Department shall give notice of the date, time, and place of the public meeting and a description of the proposal by publication in a newspaper of general circulation in the city or county where land disposal is to take place. Public notice of the scheduled meeting shall occur no fewer than seven or more than 14 days prior to the meeting. The Board shall not issue the permit for land disposal until the public meeting has been held and comment has been received from the local governing body, or until 30 days have lapsed from the date of the public meeting. This section shall not apply to applications for septic tank permits.</p><p>B. When a farm is to be added to an existing permit authorizing land application of sewage sludge, the Department shall notify persons residing on property bordering such farm, and shall receive written comments from those persons for a period not to exceed 30 days. Based upon the written comments, the Department shall determine whether additional site-specific requirements should be included in the authorization for land application at the farm.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0929" target="_blank" rel="noreferrer">929</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0042" target="_blank" rel="noreferrer">42</a>.</p>',4),r=[n];function s(l,c,d,p,h,f){return t(),a("div",null,r)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
