import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 62.1-44.26 Hearings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.26.md","filePath":"vacode/62.1-44.26.md"}'),o={name:"vacode/62.1-44.26.md"},n=t('<h1 id="§-62-1-44-26-hearings" tabindex="-1">§ 62.1-44.26 Hearings. <a class="header-anchor" href="#§-62-1-44-26-hearings" aria-label="Permalink to &quot;§ 62.1-44.26 Hearings.&quot;">​</a></h1><p>A. (For expiration date -- see notes) The formal hearings held under this chapter shall be conducted pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4009/" target="_blank" rel="noreferrer">2.2-4009</a> or <a href="http://law.lis.virginia.gov/vacode/2.2-4020/" target="_blank" rel="noreferrer">2.2-4020</a> and may be conducted by the Board itself at a regular or special meeting of the Board, or by at least one member of the Board designated by the chairman to conduct such hearings on behalf of the Board at any other time and place authorized by the Board.</p><p>A. (For effective date -- see notes) The formal hearings held by the Board under this chapter shall be conducted pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4009/" target="_blank" rel="noreferrer">2.2-4009</a> or <a href="http://law.lis.virginia.gov/vacode/2.2-4020/" target="_blank" rel="noreferrer">2.2-4020</a> and may be conducted by the Board itself at a regular or special meeting of the Board, or by at least one member of the Board designated by the chairman to conduct such hearings on behalf of the Board at any other time and place authorized by the Board.</p><p>B. A verbatim record of the proceedings of such hearings shall be taken and filed with the Board. Depositions may be taken and read as in actions at law.</p><p>C. The Board shall have power to issue subpoenas and subpoenas duces tecum, and at the request of any party shall issue such subpoenas. The failure of a witness without legal excuse to appear or to testify or to produce documents shall be acted upon by the Board in the manner prescribed in § <a href="http://law.lis.virginia.gov/vacode/2.2-4022/" target="_blank" rel="noreferrer">2.2-4022</a>. Witnesses who are subpoenaed shall receive the same fees and mileage as in civil actions.</p><p>1970, c. 638; 1977, c. 291; 1993, c. 897; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0276" target="_blank" rel="noreferrer">276</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0557" target="_blank" rel="noreferrer">557</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',6),i=[n];function s(l,h,d,c,p,g){return a(),r("div",null,i)}const b=e(o,[["render",s]]);export{u as __pageData,b as default};
