import{_ as t,o as a,c as o,z as e,a as n}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 62.1-44.27 Rules of evidence in hearings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.27.md","filePath":"vacode/62.1-44.27.md"}'),i={name:"vacode/62.1-44.27.md"},s=e("h1",{id:"§-62-1-44-27-rules-of-evidence-in-hearings",tabindex:"-1"},[n("§ 62.1-44.27 Rules of evidence in hearings. "),e("a",{class:"header-anchor",href:"#§-62-1-44-27-rules-of-evidence-in-hearings","aria-label":'Permalink to "§ 62.1-44.27 Rules of evidence in hearings."'},"​")],-1),r=e("p",null,"In all hearings under this chapter:",-1),d=e("p",null,"(1) All relevant and material evidence shall be received, except that (a) the rules relating to privileged communications and privileged topics shall be observed; (b) hearsay evidence shall be received only if the declarant is not readily available as a witness; and (c) secondary evidence of the contents of a document shall be received only if the original is not readily available. In deciding whether a witness or document is readily available, the Board or hearing officer shall balance the importance of the evidence against the difficulty of obtaining it, and the more important the evidence is the more effort should be made to produce the eyewitness or the original document.",-1),c=e("p",null,"(2) All reports of inspectors and subordinates of the Board and other records and documents in the possession of the Board bearing on the case shall be introduced by the Board at the hearing.",-1),l=e("p",null,"(3) Subject to the provisions of subdivision (1) of this section every party shall have the right to cross-examine adverse witnesses and any inspector or subordinate of the Board whose report is in evidence and to submit rebuttal evidence.",-1),h=e("p",null,"(4) The decision of the Board shall be based only on evidence received at the hearing and matters of which a court of record could take judicial notice.",-1),u=e("p",null,"1970, c. 638.",-1),f=[s,r,d,c,l,h,u];function v(p,_,b,m,g,y){return a(),o("div",null,f)}const x=t(i,[["render",v]]);export{w as __pageData,x as default};
