import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 62.1-44.34:17 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.34:17.md","filePath":"vacode/62.1-44.34:17.md"}'),o={name:"vacode/62.1-44.34:17.md"},n=r('<h1 id="§-62-1-44-34-17-exemptions" tabindex="-1">§ 62.1-44.34:17 Exemptions. <a class="header-anchor" href="#§-62-1-44-34-17-exemptions" aria-label="Permalink to &quot;§ 62.1-44.34:17 Exemptions.&quot;">​</a></h1><p>A. Sections <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15/" target="_blank" rel="noreferrer">62.1-44.34:15</a> and <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:16/" target="_blank" rel="noreferrer">62.1-44.34:16</a> do not apply to a facility having a maximum storage or handling capacity of less than 25,000 gallons of oil or to a tank vessel having a maximum storage, handling or transporting capacity of less than 15,000 gallons of oil or to a tank used to contain oil for less than 120 days and only in connection with activities related to the containment and cleanup of oil or to any vessel engaged only in activities within state waters related to the containment and cleanup of oil, including response-related training or drills.</p><p>B. Facilities having a maximum storage or handling capacity of between 25,000 gallons and one million gallons of oil shall be exempt until July 1, 1993, from any requirement under § <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15/" target="_blank" rel="noreferrer">62.1-44.34:15</a> to install ground water monitoring wells or other ground water protection devices.</p><p>C. For purposes of §§ <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15/" target="_blank" rel="noreferrer">62.1-44.34:15</a> and <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:16/" target="_blank" rel="noreferrer">62.1-44.34:16</a>, the definition of oil does not include nonpetroleum hydrocarbon-based animal and vegetable oils, or petroleum, including crude oil or any fraction thereof, which is specifically listed or designated as a hazardous substance under subparagraphs (A) through (F) of section 101 (14) of the Comprehensive Environmental Response, Compensation, and Liability Act (42 U.S.C. § 9601) and which is subject to the provisions of that Act.</p><p>D. Facilities not engaged in the resale of oil from aboveground storage tanks shall not be subject to regulations promulgated pursuant to § <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15.1/" target="_blank" rel="noreferrer">62.1-44.34:15.1</a> until July 1, 1995, or any date later specified by the Board.</p><p>E. Aboveground storage tanks with a capacity of 5,000 gallons or less containing heating oil for consumption on the premises where stored shall be exempt from the provisions of § <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15.1/" target="_blank" rel="noreferrer">62.1-44.34:15.1</a>.</p><p>F. For purposes of §§ <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15.1/" target="_blank" rel="noreferrer">62.1-44.34:15.1</a> and <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:16/" target="_blank" rel="noreferrer">62.1-44.34:16</a>, and for the purposes of any requirement under § <a href="http://law.lis.virginia.gov/vacode/62.1-44.34:15/" target="_blank" rel="noreferrer">62.1-44.34:15</a> to install ground water monitoring wells, ground water protection devices, or to conduct ground water characterization studies, the definition of oil does not include asphalt and asphalt compounds which are not liquid at standard conditions of temperature and pressure (sixty degrees Fahrenheit and <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP14.7" target="_blank" rel="noreferrer">14.7</a> pounds per square inch absolute).</p><p>1990, c. 917; 1992, c. 456; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0277" target="_blank" rel="noreferrer">277</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>.</p>',8),i=[n];function l(s,d,c,g,p,h){return a(),t("div",null,i)}const v=e(o,[["render",l]]);export{u as __pageData,v as default};
