import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 62.1-44.34:25 Virginia Spill Response Council created; purpose; membership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.34:25.md","filePath":"vacode/62.1-44.34:25.md"}'),o={name:"vacode/62.1-44.34:25.md"},t=a('<h1 id="§-62-1-44-34-25-virginia-spill-response-council-created-purpose-membership" tabindex="-1">§ 62.1-44.34:25 Virginia Spill Response Council created; purpose; membership. <a class="header-anchor" href="#§-62-1-44-34-25-virginia-spill-response-council-created-purpose-membership" aria-label="Permalink to &quot;§ 62.1-44.34:25 Virginia Spill Response Council created; purpose; membership.&quot;">​</a></h1><p>A. There is hereby created the Virginia Spill Response Council. The purpose of the Council is to (i) improve the Commonwealth&#39;s capability to respond in a timely and coordinated fashion to incidents involving the discharge of oil or hazardous materials which pose a threat to the environment, its living resources, and the health, safety, and welfare of the people of the Commonwealth and (ii) provide an ongoing forum for discussions between agencies which are charged with the prevention of, and response to, oil spills and hazardous materials incidents, and those agencies responsible for the remediation of such incidents.</p><p>B. The Secretary of Natural and Historic Resources and the Secretary of Public Safety and Homeland Security, upon the advice of the director of the agency, shall select one representative from each of the following agencies to serve as a member of the Council: Department of Emergency Management, State Water Control Board, Department of Environmental Quality, Virginia Marine Resources Commission, Department of Wildlife Resources, Department of Health, Department of Fire Programs, and the Council on the Environment.</p><p>C. The Secretary of Natural and Historic Resources or his designee shall serve as chairman of the Council.</p><p>1990, c. 598; 1991, c. 66; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',5),n=[t];function s(l,c,p,h,d,f){return i(),r("div",null,n)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
