import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 62.1-44.5 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Prohibition of waste discharges or other quality alterations of state waters except as authorized by permit; notification required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-44.5.md","filePath":"vacode/62.1-44.5.md"}'),i={name:"vacode/62.1-44.5.md"},o=r('<h1 id="§-62-1-44-5-for-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-prohibition-of-waste-discharges-or-other-quality-alterations-of-state-waters-except-as-authorized-by-permit-notification-required" tabindex="-1">§ 62.1-44.5 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Prohibition of waste discharges or other quality alterations of state waters except as authorized by permit; notification required. <a class="header-anchor" href="#§-62-1-44-5-for-effective-date-see-acts-2016-cc-68-and-758-as-amended-by-acts-2017-c-345-prohibition-of-waste-discharges-or-other-quality-alterations-of-state-waters-except-as-authorized-by-permit-notification-required" aria-label="Permalink to &quot;§ 62.1-44.5 (For effective date, see Acts 2016, cc. 68 and 758, as amended by Acts 2017, c. 345) Prohibition of waste discharges or other quality alterations of state waters except as authorized by permit; notification required.&quot;">​</a></h1><p>A. Except in compliance with a certificate, land-disturbance approval, or permit issued by the Board or other entity authorized by the Board to issue a certificate, land-disturbance approval, or permit pursuant to this chapter, it shall be unlawful for any person to:</p><ol><li><p>Discharge into state waters sewage, industrial wastes, other wastes, or any noxious or deleterious substances;</p></li><li><p>Excavate in a wetland;</p></li><li><p>Otherwise alter the physical, chemical, or biological properties of state waters and make them detrimental to the public health, or to animal or aquatic life, or to the uses of such waters for domestic or industrial consumption, or for recreation, or for other uses;</p></li><li><p>On and after October 1, 2001, conduct the following activities in a wetland:</p></li></ol><p>a. New activities to cause draining that significantly alters or degrades existing wetland acreage or functions;</p><p>b. Filling or dumping;</p><p>c. Permanent flooding or impounding; or</p><p>d. New activities that cause significant alteration or degradation of existing wetland acreage or functions; or</p><ol start="5"><li>Discharge stormwater into state waters from Municipal Separate Storm Sewer Systems or land disturbing activities.</li></ol><p>B. Any person in violation of the provisions of subsection A who discharges or causes or allows (i) a discharge of sewage, industrial waste, other wastes, or any noxious or deleterious substance into or upon state waters or (ii) a discharge that may reasonably be expected to enter state waters shall, upon learning of the discharge, promptly notify, but in no case later than 24 hours the Board, the Director of the Department of Environmental Quality, or the coordinator of emergency services appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/44-146.19/" target="_blank" rel="noreferrer">44-146.19</a> for the political subdivision reasonably expected to be affected by the discharge. Written notice to the Director of the Department of Environmental Quality shall follow initial notice within the time frame specified by the federal Clean Water Act.</p><p>Code 1950, § 62.1-17; 1968, c. 659; 1970, c. 638; 1990, c. 717; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0119" target="_blank" rel="noreferrer">119</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1032" target="_blank" rel="noreferrer">1032</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1054" target="_blank" rel="noreferrer">1054</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0383" target="_blank" rel="noreferrer">383</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0372" target="_blank" rel="noreferrer">372</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>.</p>',10),n=[o];function s(c,l,f,d,h,p){return t(),a("div",null,n)}const b=e(i,[["render",s]]);export{u as __pageData,b as default};
