import{_ as e,o as a,c as i,O as s}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 62.1-69.27 Commission purposes and mission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/62.1-69.27.md","filePath":"vacode/62.1-69.27.md"}'),o={name:"vacode/62.1-69.27.md"},n=s('<h1 id="§-62-1-69-27-commission-purposes-and-mission" tabindex="-1">§ 62.1-69.27 Commission purposes and mission. <a class="header-anchor" href="#§-62-1-69-27-commission-purposes-and-mission" aria-label="Permalink to &quot;§ 62.1-69.27 Commission purposes and mission.&quot;">​</a></h1><p>The Commission&#39;s purposes and mission shall be to provide guidance for the stewardship and enhancement of the water quality and natural resources of the Rappahannock River Basin. The Commission shall be a forum in which local governments and citizens can discuss issues affecting the Basin&#39;s water quality and quantity and other natural resources. Through promoting communication, coordination and education, and by suggesting appropriate solutions to identified problems, the Commission shall promote activities by local, state and federal governments, and by individuals, that foster resource stewardship for the environmental and economic health of the Basin.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0386" target="_blank" rel="noreferrer">386</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0456" target="_blank" rel="noreferrer">456</a>.</p>',3),t=[n];function r(c,l,d,p,m,h){return a(),i("div",null,t)}const f=e(o,[["render",r]]);export{_ as __pageData,f as default};
